JavaScript<pre><code>let str = "Hola, món!";
console.log(str.length); // Mostra: 12
/************************************/
let str = "Hola";
console.log(str.charAt(1)); // Mostra: "o" (el caràcter a la posició 1)
/************************************/
let str = "Hola, món!";
let subStr = str.substring(0, 4); // subStr conté "Hola"
/************************************/
let str = "Hola, món!";
let subStr = str.slice(0, 4); // subStr conté "Hola"
/************************************/
let str = "Hola";
console.log(str.toUpperCase()); // Mostra: "HOLA"
/************************************/
let str = "Hola";
console.log(str.toLowerCase()); // Mostra: "hola"
/************************************/
let str1 = "Hola";
let str2 = "món";
let result = str1.concat(", ", str2); // result conté "Hola, món"
/************************************/
let str = "Hola, món!";
let parts = str.split(","); // parts conté ["Hola", " món!"]
/************************************/
let str = "Hola, món!";
console.log(str.indexOf("món")); // Mostra: 6
/************************************/
let str = "Hola, món!";
let newStr = str.replace("món", "univers"); // newStr conté "Hola, univers!"
</code></pre>
